import React from "react";

function PopupWithForm ({isOpen, isInputValid, onCloseButton, onSubmit, name, title, buttonText, children}) {

  return (
    <div className={`popup ${isOpen && 'popup_viewable'}`} id={`popup_${name}`}>
        <div className="popup__window">
            <button onClick={onCloseButton} aria-label="Закрыть" className="close-button" id={`close-button_${name}`}></button>
            <h2 className="popup__title">{title}</h2>
            <form onSubmit={onSubmit} className="popup__form" id={`popup__form_${name}`} noValidate>
                {children}
                <button aria-label={buttonText} className={`submit-button ${isInputValid || avatarButtonToggle || 'inactive-button'}`} type="submit" disabled={!isInputValid || avatarButtonToggle}>
                        {buttonText}
                </button>
            </form>
        </div>
    </div>
  )
}

export default PopupWithForm;
