import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../context/CurrentUserContext';

function EditProfilePopup ({onCloseButton, onUpdateUser, isOpen}) {
    const currentUser = useContext(CurrentUserContext);

    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    function handleChangeName(event) {
        setName(event.target.value);
    }
    function handleChangeDescription(event) {
        setDescription(event.target.value);
    }

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name: name,
            about: description,
        })
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    return (
        <PopupWithForm 
        onSubmit={handleSubmit} 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen} 
        name="edit-profile" 
        title="Редактировать профиль" 
        buttonText="Сохранить">
            <input onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required />
            <span className="popup__input-error name-input-error"></span>
            <input onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required />
            <span className="popup__input-error profession-input-error"></span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
