import Header from './Header';
import Footer from './Footer';
import Main from './Main';
import PopupWithForm from './PopupWithForm';
import PopupWithImage from './PopupWithImage';
import { api } from "../utils/Api";

import React from "react";

function App() {

    const [currentUser, setCurrentUser] = React.useState({});

    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);

    const [selectedCard, setSelectedCard] = React.useState(null);

    function onEditAvatar() {
        setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
    }

    function onEditProfile() {
        setEditProfilePopupOpen(!isEditProfilePopupOpen);
    }

    function onAddPlace() {
        setAddPlacePopupOpen(!isAddPlacePopupOpen);
    }

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function closeAllPopups() {
        if (isEditAvatarPopupOpen) setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
        else if (isEditProfilePopupOpen) setEditProfilePopupOpen(!isEditProfilePopupOpen);
        else if (isAddPlacePopupOpen) setAddPlacePopupOpen(!isAddPlacePopupOpen);
        else if (selectedCard) setSelectedCard(!selectedCard);
    }

    React.useEffect(() => {
        api.getUserInfo()
        .then((userData) => {
            setCurrentUser(userData);
        })
        .catch(err => {
            console.log(err);
        });
    }, [])

  return (

      <div className="body">
        <div className="page">

            <Header />

            <Main 
                    onEditAvatar = {onEditAvatar} 
                    onEditProfile = {onEditProfile}
                    onAddPlace = {onAddPlace}
                    onCloseButton = {closeAllPopups}
                    onCardClick={handleCardClick}
                >

                <PopupWithForm 
                onCloseButton = {closeAllPopups} 
                isOpen={isEditProfilePopupOpen} 
                name="edit-profile" 
                title="Редактировать профиль"
                buttonText="Сохранить">
                    <input className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required />
                    <span className="popup__input-error name-input-error"></span>
                    <input className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required />
                    <span className="popup__input-error profession-input-error"></span>
                </PopupWithForm>

                <PopupWithForm 
                onCloseButton = {closeAllPopups} 
                isOpen={isAddPlacePopupOpen} 
                name="add-photo" 
                title="Новое место"
                buttonText="Создать">
                    <input className="popup__input" id="place-input" placeholder="Название" type="text" name="place" minLength="2" maxLength="30" required />
                    <span className="popup__input-error place-input-error"></span>
                    <input className="popup__input" id="url-input" placeholder="Ссылка на картинку" type="url" name="imageUrl" required />
                    <span className="popup__input-error url-input-error"></span>
                </PopupWithForm>

                <PopupWithForm 
                onCloseButton = {closeAllPopups} 
                isOpen={isEditAvatarPopupOpen} 
                name="add-avatar" 
                title="Обновить аватар"
                buttonText="Сохранить">
                    <input className="popup__input" id="avatar-input" placeholder="Ссылка на новый аватар" type="url" name="avatarUrl" required />
                    <span className="popup__input-error avatar-input-error"></span>
                </PopupWithForm>

                <PopupWithForm 
                name="delete-photo" 
                title="Вы уверены?"
                buttonText="Да">
                </PopupWithForm>

                <PopupWithImage onClose={closeAllPopups} card={selectedCard}></PopupWithImage>

            </Main> 

            <Footer />

        </div>

    </div>
  );
}

export default App;
