import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup ({isOpen, onCloseButton, onCardAdd}) {

    const newCardDescription = React.useRef();
    const newCardUrl = React.useRef();

    const newCard = {
        name: newCardDescription.current.value,
        link: newCardUrl.current.value
    }

    function handleSubmit(event) {
        event.preventDefault();

        onCardAdd(newCard)
    }

    return (
        <PopupWithForm 
        onSubmit={handleSubmit}
        onCloseButton = {onCloseButton} 
        isOpen={isOpen}
        onCardAdd={onCardAdd}
        name="add-photo" 
        title="Новое место"
        buttonText="Создать">
            <input ref={newCardDescription} className="popup__input" id="place-input" placeholder="Название" type="text" name="place" minLength="2" maxLength="30" required />
            <span className="popup__input-error place-input-error"></span>
            <input ref={newCardUrl} className="popup__input" id="url-input" placeholder="Ссылка на картинку" type="url" name="imageUrl" required />
            <span className="popup__input-error url-input-error"></span>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
