import React, {useEffect, useState} from 'react';

function EditProfilePopup ({onCloseButton, isOpen}) {

    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    function handleChange(event) {
        setName(event.target.value);
        setDescription(event.target.value);
    }

    return (
        <PopupWithForm onCloseButton = {onCloseButton} isOpen={isOpen} name="edit-profile" title="Редактировать профиль" buttonText="Сохранить">
            <input onChange={handleChange} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required />
            <span className="popup__input-error name-input-error"></span>
            <input onChange={handleChange} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required />
            <span className="popup__input-error profession-input-error"></span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
