import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../context/CurrentUserContext';

function EditProfilePopup ({onCloseButton, onUpdateUser, isOpen}) {
    const currentUser = useContext(CurrentUserContext);




    const profileInputName = React.useRef();
    const profileInputDescription = React.useRef();

    const [isInputValid, setValidity] = useState(true);

    // function handleChange() {
    //     console.log(profileInput.current.validity);
    //     // console.log(profileInput.current);
    //     console.log(profileInput.current.validationMessage);

    //     setValidity(profileInput.current.validity.valid);        
    // }





    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    function handleChangeName(event) {
        setName(event.target.value);
       // handleChange();
        setValidity(profileInputName.current.validity.valid);        

    }
    function handleChangeDescription(event) {
        setDescription(event.target.value);
       // handleChange();
        setValidity(profileInputDescription.current.validity.valid);        

    }

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name: name,
            about: description,
        })
    }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    return (
        <PopupWithForm 
        onSubmit={handleSubmit} 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen}
        isInputValid={isInputValid}
        name="edit-profile" 
        title="Редактировать профиль" 
        buttonText="Сохранить">
            {/* <input ref={profileInput} onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required />
            <span className="popup__input-error name-input-error"></span>
            <input ref={profileInput} onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required />
            <span className="popup__input-error profession-input-error"></span>
             */}

            <input ref={profileInputName} onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required />
            <span className={`popup__input-error name-input-error ${isInputValid || 'popup__input-error_active'}`}></span>
            <input ref={profileInputDescription} onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required />
            <span className={`popup__input-error profession-input-error ${isInputValid || 'popup__input-error_active'}`}></span>



        </PopupWithForm>
    )
}

export default EditProfilePopup;

