// import { useEffect, useState, useContext, React } from "react";
import React, { useEffect, useState, useContext } from "react";

import { api } from "../utils/Api";
import Card from "./Card";
import {CurrentUserContext} from '../context/CurrentUserContext';


function Main ({onEditAvatar, onEditProfile, onAddPlace, onCloseButton, onCardClick, children}) {
    // console.log(CurrentUserContext);

    const currentUser = useContext(CurrentUserContext);

    // const [userName, setUserName] = useState('');
    // const [userDescription, setUserDescription] = useState('');
    // const [userAvatar, setUserAvatar] = useState('');

    const [cards, setCards] = useState([]);

    function handleCardLike (card) {
        const isLiked = card.likes.some(elem => elem._id === currentUser._id);


        api.changeLikeCardStatus(card._id, isLiked)
            .then((newCard) => {
                setCards((oldCards) => {
                    return oldCards.map((item) => item._id === card._id ? newCard : item)
                })
            })
        // api.changeLikeCardStatus(card._id, isLiked).then((newCard) => {
        //     setCards((oldCards) => oldCards.map((item) => item._id === card._id ? newCard : item))
        // })
        .catch(err => {
            console.log(err);
        });   
    }
    // api.changeLikeCardStatus(card._id, isLiked).then((newCard) => {
    //     setCards((oldCards) => oldCards.map((item) => item._id === card._id ? newCard : item))
    // })


    useEffect(() => {

        // api.getUserInfo()
        // .then(userData => {
        //     setUserName(userData.name);
        //     setUserDescription(userData.about);
        //     setUserAvatar(userData.avatar);
        // })
        // .catch(err => {
        //     console.log(err);
        // });

        api.getCards()
        .then(cardsData => {
            setCards(cardsData);
        })
        .catch(err => {
            console.log(err);
        });   
    }, []);

    return (
        <main className="main">
            <section className="profile">
                <div className="profile__updateImage" ></div>
                {/* <img onClick={onEditAvatar} className="profile__image" src={userAvatar} alt="Фото профиля" /> */}
                <img onClick={onEditAvatar} className="profile__image" src={currentUser.avatar || ''} alt="Фото профиля" />
                <div className="profile__info">
                    {/* <h1 className="profile__title">{userName}</h1> */}
                    <h1 className="profile__title">{currentUser.name || ''}</h1>

                    <button onClick={onEditProfile} aria-label="Редактировать профиль" className="edit-button"></button>
                    {/* <h2 className="profile__subtitle">{userDescription}</h2> */}
                    <h2 className="profile__subtitle">{currentUser.about || ''}</h2>
                </div>
                <button onClick={onAddPlace} aria-label="Добавить фото" className="add-button"></button>
                {children}
            </section>

            <section className="elements">
                {cards.map((card) => (
                    <Card key={card._id} card={card} onCardClick={onCardClick} onCardLike={handleCardLike}/>
                ))}
            </section>

        </main>
    );
}

export default Main;
