import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../context/CurrentUserContext';

function EditProfilePopup ({onCloseButton, onUpdateUser, isOpen}) {
    const currentUser = useContext(CurrentUserContext);



    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    const [isInputValid, setValidity] = useState(true);

    const [error, setError] = useState('');


    // function handleChangeName(event) {
    //     setName(event.target.value);
    //     //setValidity(profileInputName.current.validity.valid);        

    // }
    // function handleChangeDescription(event) {
    //     setDescription(event.target.value);
    //     //setValidity(profileInputDescription.current.validity.valid);        
    // }

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name: name,
            about: description,
        })
    }

    function handleUserInput (event) {
        if (!event.target.validity.valid) {
            setError(event.target.validity.validationMessage);
            setValidity(!error);
            console.log('отработал инпут хэндлер');
    }
      }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    return (
        <PopupWithForm 
        onSubmit={handleSubmit} 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen}
        isInputValid={isInputValid}
        name="edit-profile" 
        title="Редактировать профиль" 
        buttonText="Сохранить">
            {/* <input ref={profileInputName} onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required /> */}
            <input onChange={handleUserInput} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="name" minLength="2" maxLength="40" required />

            <span className={`popup__input-error name-input-error ${error || 'popup__input-error_active'}`}>
                {error ? {error} : ''}
            </span>
            {/* <input ref={profileInputDescription} onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required /> */}
            <input onChange={handleUserInput} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="about" minLength="2" maxLength="200" required />

            <span className={`popup__input-error profession-input-error ${error || 'popup__input-error_active'}`}>
                {/* {errorDescription ? '' : `${profileInputDescription.current.validationMessage}`} */}
                {error ? {error} : ''}
            </span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
