import React from "react";
import Header from './Header';
import Footer from './Footer';
import Main from './Main';
import PopupWithForm from './PopupWithForm';
import AddPlacePopup from "./AddPlacePopup";
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from "./EditAvatarPopup";
import PopupWithImage from './PopupWithImage';
import { api } from "../utils/Api";
import {CurrentUserContext} from '../context/CurrentUserContext';

function App() {

    const [currentUser, setCurrentUser] = React.useState({
        name: "",
        about: "",
        avatar: "",
        _id: "",
        cohort: ""
      });

    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);

    const [cards, setCards] = React.useState(false);

    const [selectedCard, setSelectedCard] = React.useState(false);

    const [isLoaded, setIsLoaded] = React.useState(false);

    function onEditAvatar() {
        setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
    }

    function onEditProfile() {
        setEditProfilePopupOpen(!isEditProfilePopupOpen);
    }

    function onAddPlace() {
        setAddPlacePopupOpen(!isAddPlacePopupOpen);
    }

    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function closeAllPopups() {
        if (isEditAvatarPopupOpen) setEditAvatarPopupOpen(!isEditAvatarPopupOpen);
        else if (isEditProfilePopupOpen) setEditProfilePopupOpen(!isEditProfilePopupOpen);
        else if (isAddPlacePopupOpen) setAddPlacePopupOpen(!isAddPlacePopupOpen);
        else if (selectedCard) setSelectedCard(!selectedCard);
    }

    function handleUpdateUser (data) {
        api.setUserInfo(data)
        .then(userData => {
            console.log('Загружаемса');
            setIsLoaded(true);
            setCurrentUser(userData);
            closeAllPopups();
        })
        .catch(err => {
            console.log(err);
        })
        .finally(() => {
            console.log('Загрузилиса');
            setIsLoaded(false);
        })
    }

    function handleUpdateAvatar (data) {
        api.setUserAvatar(data)
        .then(userData => {
            setCurrentUser(userData);
            closeAllPopups();
        })
        .catch(err => {
            console.log(err);
        });
    }

    function handleCardLike (card) {
        const isLiked = card.likes.some(elem => elem._id === currentUser._id);

        api.changeLikeCardStatus(card._id, isLiked)
        .then((newCard) => {
            setCards((oldCards) => {
                return oldCards.map((item) => item._id === card._id ? newCard : item)
            })
        })
        .catch(err => {
            console.log(err);
        });   
    }

    function handleDeleteClick (card) {
        api.deleteCard(card._id)
        .then(() => {
            setCards((oldCards) => {
                return oldCards.filter((item) => item._id != card._id)
            })
        })
        .catch(err => {
            console.log(err);
        });   
    }

    function handleAddPlaceSubmit (data) {
        api.addNewCard(data)
        .then((newCard) => {
            setCards([newCard, ...cards]);
            closeAllPopups();
        })
        .catch(err => {
            console.log(err);
        });
    }

    React.useEffect(() => {
        api.getUserInfo()
        .then(userData => {
            setCurrentUser(userData);
        })
        .catch(err => {
            console.log(err);
        });
    }, []);

    React.useEffect(() => {
        api.getCards()
        .then(cardsData => {
            setCards(cardsData);
        })
        .catch(err => {
            console.log(err);
        });   
    }, []);

  return (

      <div className="body">
        <div className="page">
        <CurrentUserContext.Provider value={currentUser}>

            <Header />

            <Main 
                onEditAvatar = {onEditAvatar} 
                onEditProfile = {onEditProfile}
                onAddPlace = {onAddPlace}
                onCloseButton = {closeAllPopups}
                onCardClick={handleCardClick}
                onCardDelete={handleDeleteClick}
                onCardLike={handleCardLike}
                cards={cards}>
            </Main> 

            <Footer />

            <EditProfilePopup isLoaded={isLoaded} onUpdateUser={handleUpdateUser} onCloseButton = {closeAllPopups} isOpen={isEditProfilePopupOpen} />

            <AddPlacePopup onCardAdd={handleAddPlaceSubmit} onCloseButton = {closeAllPopups} isOpen={isAddPlacePopupOpen} />

            <EditAvatarPopup onUpdateAvatar={handleUpdateAvatar} onCloseButton = {closeAllPopups} isOpen={isEditAvatarPopupOpen} />

            <PopupWithImage onClose={closeAllPopups} card={selectedCard}></PopupWithImage>



        </CurrentUserContext.Provider>
        </div>

    </div>
  );
}

export default App;
