import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../context/CurrentUserContext';

function EditProfilePopup ({onCloseButton, onUpdateUser, isOpen}) {
    const currentUser = useContext(CurrentUserContext);



    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    const [isInputValid, setValidity] = useState(true);

    const [nameError, setNameError] = useState('');
    const [descriptionError, setDescriptionError] = useState('');


    function handleChangeName(event) {
        setName(event.target.value);
        setValidity(event.target.validity.valid);        
        setNameError(event.target.validity.validationMessage);
    }

    function handleChangeDescription(event) {
        setDescription(event.target.value);
        setValidity(event.target.validity.valid);   
        setDescriptionError(event.target.validity.validationMessage);     
    }

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name: name,
            about: description,
        })
    }

    // function handleUserInput (event) {
    //     setName(event.target.value);
    //     setDescription(event.target.value);
    //     switch (event.target.name){
    //         case 'name':
    //             setNameError(event.target.validity.validationMessage);
    //             break;
    //         case 'about':
    //             setDescriptionError(event.target.validity.validationMessage);
    //             break;
    //     }
    //     setValidity(!(nameError || descriptionError));
    //   }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    return (
        <PopupWithForm 
        onSubmit={handleSubmit} 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen}
        isInputValid={isInputValid}
        name="edit-profile" 
        title="Редактировать профиль" 
        buttonText="Сохранить">
            {/* <input ref={profileInputName} onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required /> */}
            <input onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="name" minLength="2" maxLength="40" required />

            <span className={`popup__input-error name-input-error ${isInputValid || 'popup__input-error_active'}`}>
                {isInputValid ? '': {nameError}}
            </span>
            {/* <input ref={profileInputDescription} onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required /> */}
            <input onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="about" minLength="2" maxLength="200" required />

            <span className={`popup__input-error profession-input-error ${isInputValid || 'popup__input-error_active'}`}>
                {/* {errorDescription ? '' : `${profileInputDescription.current.validationMessage}`} */}
                {isInputValid ? '' : {descriptionError}}
            </span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
