import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup ({isOpen, onCloseButton}) {

    return (
        <PopupWithForm 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen} 
        name="add-photo" 
        title="Новое место"
        buttonText="Создать">
            <input className="popup__input" id="place-input" placeholder="Название" type="text" name="place" minLength="2" maxLength="30" required />
            <span className="popup__input-error place-input-error"></span>
            <input className="popup__input" id="url-input" placeholder="Ссылка на картинку" type="url" name="imageUrl" required />
            <span className="popup__input-error url-input-error"></span>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
