import React, { useEffect, useState } from "react";
import { api } from "../utils/Api";
import Card from "./Card";

function Main (props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    const [cards, setCards] = React.useState([]);

    React.useEffect((() => {
        api.getUserInfo()
        .then(userData => {
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
        })
        .catch(err => {
            console.log(err);
        });

        api.getCards()
        .then(cardsData => {
            setCards(cardsData);
        })
        .catch(err => {
            console.log(err);
        });   
    }), []);

    api.getCards()
    .then(cardsData => {
        setCards(cardsData);
    })
    .catch(err => {
        console.log(err);
    });   
    return (
        <main className="main">
            <section className="profile">
                <div className="profile__updateImage" ></div>
                <img onClick={props.onEditAvatar} className="profile__image" src={userAvatar} alt="Фото профиля" />
                <div className="profile__info">
                    <h1 className="profile__title">{userName}</h1>
                    <button onClick={props.onEditProfile} aria-label="Редактировать профиль" className="edit-button"></button>
                    <h2 className="profile__subtitle">{userDescription}</h2>
                </div>
                <button onClick={props.onAddPlace} aria-label="Добавить фото" className="add-button"></button>
                {props.children}
            </section>

            <section className="elements">
                {cards.map((card) => (
                    <Card key={card._id} card={card} onCardClick={props.onCardClick}/>
                ))}
            </section>

        </main>
    );
}

export default Main;
