import React from "react";

function PopupWithForm (props) {

  return (
    <div className={props.isOpen ? 'popup popup_viewable' : 'popup'} id={`popup_${props.name}`}>
        <div className="popup__window">
            <button onClick={props.onCloseButton} aria-label="Закрыть" className="close-button" id={`close-button_${props.name}`}></button>
            <h2 className="popup__title">{props.title}</h2>
            <form className="popup__form" id={`popup__form_${props.name}`} noValidate>
                {props.children}
                <button aria-label={props.buttonText} className="save-button submit-button" type="submit">
                        {props.buttonText}
                </button>
            </form>
        </div>
    </div>
  )
}

export default PopupWithForm;
