import React, {useEffect, useState, useContext} from 'react';
import PopupWithForm from './PopupWithForm';
import { CurrentUserContext } from '../context/CurrentUserContext';

function EditProfilePopup ({onCloseButton, onUpdateUser, isOpen}) {




    const [state, setState] = useState({name: '', description: ''});



    const currentUser = useContext(CurrentUserContext);
    const [isInputValid, setValidity] = useState(true);

    // const profileInputName = React.useRef();
    // const profileInputDescription = React.useRef();

    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    function handleChangeName(event) {
        setName(event.target.value);
        //setValidity(profileInputName.current.validity.valid);        

    }
    function handleChangeDescription(event) {
        setDescription(event.target.value);
        //setValidity(profileInputDescription.current.validity.valid);        
    }

    function handleSubmit(event) {
        event.preventDefault();

        onUpdateUser({
            name: name,
            about: description,
        })
    }

    function handleUserInput (event) {
        const name = event.target.name;
        const value = event.target.value;
        setState({[name]: value});
      }

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser])

    return (
        <PopupWithForm 
        onSubmit={handleSubmit} 
        onCloseButton = {onCloseButton} 
        isOpen={isOpen}
        isInputValid={isInputValid}
        name="edit-profile" 
        title="Редактировать профиль" 
        buttonText="Сохранить">
            {/* <input ref={profileInputName} onChange={handleChangeName} value={name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="user" minLength="2" maxLength="40" required /> */}
            <input onChange={handleUserInput} value={state.name} className="popup__input" id="name-input" placeholder="Имя" type="text" name="name" minLength="2" maxLength="40" required />

            <span className={`popup__input-error name-input-error ${isInputValid || 'popup__input-error_active'}`}>
                {isInputValid ? '' : `${profileInputName.current.validationMessage}`}
            </span>
            {/* <input ref={profileInputDescription} onChange={handleChangeDescription} value={description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="profession" minLength="2" maxLength="200" required /> */}
            <input onChange={handleUserInput} value={state.description} className="popup__input" id="profession-input" placeholder="Профессия" type="text" name="about" minLength="2" maxLength="200" required />

            <span className={`popup__input-error profession-input-error ${isInputValid || 'popup__input-error_active'}`}>
                {isInputValid ? '' : `${profileInputDescription.current.validationMessage}`}
            </span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
